import { AucDialogPosition } from './types';
import { AucCustomizeDialogOverlayConfigInterface } from './interfaces';

export class AucDialogConfig<D = any> {
  data?: D; // Data for injected component.
  width?: string; // '300px', '50%', '1rem' ...
  height?: string; // '300px', '50%', '1rem' ...
  minWidth?: string; // '300px', '50%', '1rem' ...
  minHeight?: string; // '300px', '50%', '1rem' ...
  maxWidth?: string; // '300px', '50%', '1rem' ...
  maxHeight?: string; // '300px', '50%', '1rem' ...
  position?: AucDialogPosition;
  dialogClass?: string | string[]; // Add custom class to the dialog container.
  panel?: {
    panelClass: string | string[]; // Add custom class to the dialog panel.
  };
  overlay?: AucCustomizeDialogOverlayConfigInterface; // customize overlay.
}

import { Injector, Type, InjectionToken, InjectFlags } from '@angular/core';

export class AucDialogInjector implements Injector {
  constructor(
    private _parentInjector: Injector,
    private _additionalTokens: WeakMap<any, any>
  ) {
  }

  get<T>(
    token: Type<T> | InjectionToken<T>,
    notFoundValue?: T,
    flags?: InjectFlags
  ): T;
  get(token: any, notFoundValue?: any);
  get<T>(token: any, notFoundValue?: any, flags?: any): T {
    const value = this._additionalTokens.get(token);

    if (value) {
      return value;
    }

    return this._parentInjector.get<T>(token, notFoundValue);
  }
}

import { Observable, Subject } from 'rxjs';

export class AucDialogRef<R = any> {
  private readonly _afterClosed = new Subject<R>();
  private readonly _afterOpened = new Subject<null>();

  /**
   * {@link afterClosed} - emits after dialog was closed.
   * You can add subscription for it if needed. this.dialogRed.afterClosed.subscribe();
   */
  public afterClosed: Observable<any> = this._afterClosed.asObservable();

  /**
   * {@link afterOpened} - emits after dialog was opened.
   * You can add subscription for it if needed. this.dialogRed._afterOpened.subscribe();
   */
  public afterOpened: Observable<null> = this._afterOpened.asObservable();

  /**
   * {@link close} - emits close dialog action the dialog window.
   * @param result - the value which will be emitted for afterClosed action.
   */
  public close(result?: R): void {
    this._afterClosed.next(result);
  }

  /**
   * {@link open} - emits open dialog action the dialog window.
   * You mustn't use it. It's already used inside injection strategy.
   */
  public open(): void {
    this._afterOpened.next(null);
  }
}

