import {
  AfterViewInit,
  Directive,
  EmbeddedViewRef,
  Inject,
  OnDestroy,
  TemplateRef,
  ViewContainerRef
} from '@angular/core';
import { DOCUMENT } from '@angular/common';

import { AucBlockScrollHelperService } from '../../helpers';


@Directive({
  selector: '[aucContentBody]',
  exportAs: 'aucContentBody',
  providers: [ AucBlockScrollHelperService ]
})
export class AucContentBodyDirective implements AfterViewInit, OnDestroy {
  private _embeddedViewRef: EmbeddedViewRef<any>;

  constructor(private _templateRef: TemplateRef<any>,
              private _viewContainerRef: ViewContainerRef,
              @Inject(DOCUMENT) private _document: Document,
              private _blockScrollHelperService: AucBlockScrollHelperService) {
  }

  ngAfterViewInit(): void {
    this._embeddedViewRef = this._viewContainerRef.createEmbeddedView(this._templateRef);
    this._embeddedViewRef.detectChanges();

    this._embeddedViewRef.rootNodes.forEach(node => this._document.body.appendChild(node));

    this._blockScrollHelperService.lockScroll();
  }

  public destroy(): void {
    this._blockScrollHelperService.unlockScroll();
    this._embeddedViewRef.destroy();
  }

  ngOnDestroy(): void {
    this.destroy();
  }

}

import { Directive, ElementRef, Input, Renderer2 } from '@angular/core';


@Directive({
  selector: '[aucCustomClass]',
  exportAs: 'aucCustomClass'
})
export class AucCustomClassDirective {
  /**
   * {@link aucCustomClass} - It's an `@Input()` parameter.
   * Sets class / list of classes to the elements.
   */
  @Input()
  public aucCustomClass: string | string[];

  constructor(private _renderer2: Renderer2, private _elementRef: ElementRef) {
  }

  ngOnInit(): void {
    this.setClasses([].concat(this.aucCustomClass ?? []));
  }

  public setClasses(classes: string[]): void {
    if (!classes?.length) {
      return;
    }

    classes.forEach((item: string) => {
      this._renderer2.addClass(this._elementRef.nativeElement, item);
    });
  }

  public removeClasses(classes: string[]): void {
    if (!classes?.length) {
      return;
    }

    classes.forEach((item: string) => {
      this._renderer2.removeClass(this._elementRef.nativeElement, item);
    });
  }
}

import { Directive, ElementRef, Input, OnInit, Renderer2 } from '@angular/core';

import { AUC_CUSTOMIZE_DIALOG_CONFIG_KEYS } from '../../enums';
import { AucCustomizeDialogConfig } from '../../interfaces';


/**
 * Uses for customization dialog window by config.
 */
@Directive({
  selector: '[aucCustomizeDialog]',
  exportAs: 'aucCustomizeDialog'
})
export class AucCustomizeDialogDirective implements OnInit {
  /**
   * {@link config} - configuration data for customization dialog.
   */
  @Input()
  public config?: AucCustomizeDialogConfig;

  private get isConfig(): boolean {
    return !!(Object.keys(this.config ?? {})).length;
  }

  constructor(private _renderer2: Renderer2, private _elementRef: ElementRef) {
  }

  ngOnInit(): void {
    this.mapConfig();
  }

  public setClasses(classes: string[]): void {
    if (!classes.length) {
      return;
    }

    classes.forEach((item: string) => {
      this._renderer2.addClass(this._elementRef.nativeElement, item);
    });
  }

  public setStyle(style: string, value: string | number): void {
    if (!this._elementRef?.nativeElement || !style || (!value && value !== 0)) {
      return;
    }

    this._renderer2.setStyle(this._elementRef.nativeElement, style, value);
  }

  public mapConfig(): void {
    if (!this.isConfig) {
      return;
    }

    Object.keys(this.config).forEach((key: string) => {
      if (key === AUC_CUSTOMIZE_DIALOG_CONFIG_KEYS.CLASSES) {
        this.setClasses([].concat(this.config[key]));

        return;
      }

      if (key === AUC_CUSTOMIZE_DIALOG_CONFIG_KEYS.WIDTH) {
        this.setStyle('width', this.config[key]);

        return;
      }

      if (key === AUC_CUSTOMIZE_DIALOG_CONFIG_KEYS.HEIGHT) {
        this.setStyle('height', this.config[key]);

        return;
      }

      if (key === AUC_CUSTOMIZE_DIALOG_CONFIG_KEYS.MIN_WIDTH) {
        this.setStyle('min-width', this.config[key]);

        return;
      }

      if (key === AUC_CUSTOMIZE_DIALOG_CONFIG_KEYS.MIN_HEIGHT) {
        this.setStyle('min-height', this.config[key]);

        return;
      }

      if (key === AUC_CUSTOMIZE_DIALOG_CONFIG_KEYS.MAX_WIDTH) {
        this.setStyle('max-width', this.config[key]);

        return;
      }

      if (key === AUC_CUSTOMIZE_DIALOG_CONFIG_KEYS.MAX_HEIGHT) {
        this.setStyle('max-height', this.config[key]);

        return;
      }

      if (key === AUC_CUSTOMIZE_DIALOG_CONFIG_KEYS.POSITION) {
        this.setClasses([].concat(this.config[key]));

        return;
      }
    });
  }

}

