import { Directive, ElementRef, HostListener, Input, Renderer2 } from '@angular/core';


@Directive({
  selector: '[aucRipple]',
  exportAs: 'aucRipple'
})
export class AucRippleDirective {
  /**
   * {@link customClass} - It's an `@Input()` parameter.
   * Sets custom class to the ripple element.
   * This is an optional parameter.
   */
  @Input() customClass?: string;

  private readonly rippleClass = 'auc-ripple';

  @HostListener('mousedown', [ '$event' ]) onMousedown(e) {
    e.preventDefault();
    e.stopPropagation();
    this.createRipple(e);
  }

  constructor(private _renderer2: Renderer2, private _elementRef: ElementRef) {
    this.setStyle(this._elementRef.nativeElement, 'overflow', 'hidden');
    this.setStyle(this._elementRef.nativeElement, 'position', 'relative');
  }

  public setStyle(el: HTMLElement, style: string, value: string | number): void {
    if (!this._elementRef?.nativeElement || !style || (!value && value !== 0)) {
      return;
    }

    this._renderer2.setStyle(el, style, value);
  }

  private createRipple(event): void {
    const rippleIndex = [ ...this._elementRef.nativeElement.childNodes ]
      .findIndex(node => node.className === this.rippleClass);

    if (rippleIndex !== -1) {
      this._renderer2.removeChild(
        this._elementRef.nativeElement,
        this._elementRef.nativeElement.childNodes[rippleIndex]
      );
    }

    const button = this._elementRef.nativeElement;
    const circle: HTMLElement = this._renderer2.createElement('span');
    const diameter = Math.max(button.clientWidth, button.clientHeight);
    const radius = diameter / 2;
    const circleSize = `${diameter}px`;
    this._renderer2.setStyle(circle, 'width', circleSize);
    this._renderer2.setStyle(circle, 'height', circleSize);
    this._renderer2.setStyle(circle, 'left', `${event.offsetX - radius}px`);
    this._renderer2.setStyle(circle, 'top', `${event.offsetY - radius}px`);
    this._renderer2.addClass(circle, this.rippleClass);

    if ((this.customClass ?? '').trim()) {
      this._renderer2.addClass(circle, this.customClass.trim());
    }

    this._renderer2.appendChild(this._elementRef.nativeElement, circle);
  }

}

import { Directive, ElementRef, Input, OnChanges } from '@angular/core';

import { AucSetStyleProp } from './interfaces';


@Directive({
  selector: '[aucSetStyleProps]',
  exportAs: 'aucSetStyleProps'
})
export class AucSetStylePropsDirective implements OnChanges {
  /**
   * {@link aucSetStyleProps} - It's an `@Input()` parameter.
   * Used for setting style properties to the element.
   * You can use set properties in your scss.
   * Ex: aucSetStyleProps = {name: '--text-color: 'red'}.
   * In your scss file you can use it: color: var(--text-color);
   */
  @Input()
  public aucSetStyleProps!: AucSetStyleProp | AucSetStyleProp[];

  constructor(private _elementRef: ElementRef) {
  }

  ngOnChanges(): void {
    this.setProperty([].concat(this.aucSetStyleProps ?? []));
  }

  public setProperty(props: AucSetStyleProp[]): void {
    if (!Array.isArray(props) || !props.length || !this._elementRef?.nativeElement?.style) {
      return;
    }

    props.forEach((item: AucSetStyleProp) => {
      if (!item.name || !item.value) {
        return
      }

      this._elementRef.nativeElement.style.setProperty(item.name, item.value);
    });
  }

}

import { Directive, ElementRef, EventEmitter, HostListener, Input, Output, Renderer2 } from '@angular/core';
import { BehaviorSubject } from 'rxjs';

import { AucCustomClassDirective } from '../custom-class';


@Directive({
  selector: '[aucTrigger]',
  exportAs: 'aucTrigger'
})
export class AucTriggerDirective {
  /**
   * {@link triggerClass} - It's an `@Input()` parameter.
   * You can customize element when opened status.
   * This is an optional parameter. By default, used auc-trigger-opened.
   */
  @Input()
  public triggerClass?: string = 'auc-trigger-opened';

  /**
   * {@link onShowHide} - It's an `@Output()` parameter.
   * Emits show/hide state.
   * If opened - true.
   * If closed - false.
   */
  @Output()
  public onShowHide: EventEmitter<boolean> = new EventEmitter<boolean>();

  private _customClass: AucCustomClassDirective;
  private _isOpened: boolean = false;

  public opened$: BehaviorSubject<boolean> = new BehaviorSubject<boolean>(this._isOpened);

  public get opened(): boolean {
    return this._isOpened;
  }

  private set _opened(opened: boolean) {
    this._isOpened = opened;
    this.opened$.next(this.opened);
    this.onShowHide.emit(this.opened);
  }

  @HostListener('click', [ '$event' ]) onClick(e: MouseEvent): void {
    e.preventDefault();
    e.stopPropagation();

    this.showHide();
  }

  public get nativeElement() {
    return this._elementRef?.nativeElement;
  }

  constructor(private _renderer2: Renderer2, private _elementRef: ElementRef) {
    this._customClass = new AucCustomClassDirective(this._renderer2, this._elementRef);
  }

  public showHide(isOpen?: boolean): void {
    if ((isOpen ?? null) !== null) {
      this._opened = isOpen;
    } else {
      this._opened = !this._opened;
    }

    if (this._customClass) {
      if (this.opened) {
        this._customClass.setClasses([ this.triggerClass ]);

        return;
      }

      this._customClass.removeClasses([ this.triggerClass ]);
    }
  }

}

