  &.auc-account-dialog,
  &.auc-recent-transactions-dialog {
    padding: 0;
  }

  &.top {
    top: 15px;
    transform: translate(-50%, 0);
  }

  &.bottom {
    top: unset;
    bottom: 15px;
    transform: translate(-50%, 0);
  }

  &.left {
    left: 15px;
    transform: translate(0, -50%);
  }

  &.right {
    left: unset;
    right: 15px;
    transform: translate(0, -50%);
  }

  &.top-left {
    left: 15px;
    top: 15px;
    transform: translate(0, 0);
  }

  &.top-right {
    left: unset;
    right: 15px;
    top: 15px;
    transform: translate(0, 0);
  }

  &.bottom-left {
    left: 15px;
    top: unset;
    bottom: 15px;
    transform: translate(0, 0);
  }

  &.bottom-right {
    left: unset;
    right: 15px;
    top: unset;
    bottom: 15px;
    transform: translate(0, 0);
  }
}

@media (max-width: 767px) {
  .auc-dialog {
    width: 100% !important;
    min-width: 100% !important;
    height: 100vh !important;
    max-width: 100vw !important;
    max-height: 100vh !important;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    transform: translate(0, 0);

    &.top,
    &.bottom,
    &.left,
    &.right,
    &.top-left,
    &.top-right,
    &.bottom-left,
    &.bottom-right {
      top: 0;
      right: 0;
      bottom: 0;
      left: 0;
      transform: translate(0, 0);
    }
  }
}

