    if (this.components && this.components.length > 0 && !this.tabsToShow.includes('api')) {
      this.tabsToShow.push('api');
    }

    this.prettyPrint();
  }

  /**
   * Pretty prints the example text again.
   */
  public prettyPrint(): void {
    window.PR?.prettyPrint();
  }
}

